import { useHistory, Link } from 'react-router-dom';
import * as C from './styles';
import { useForm, FormActions } from '../../contexts/FormContext';
import { Theme } from '../../components/Theme';
import { ChangeEvent, useEffect, useState } from 'react';



export const FormStep3 = () => {
    const history = useHistory();
    const { state, dispatch } = useForm();

    useEffect(() => {
        if(state.name === '') {
            history.push('/');
        } else {
            dispatch({
                type: FormActions.setCurrentStep,
                payload: 3
            });
        }
    }, []);

    const handleNextStep = () => {
        if(state.email !== '' && state.github !== '') {
            console.log(state);
        } else {
            alert("Preencha os dados");
        }
    }

    const handleEmailChange = (e: ChangeEvent<HTMLInputElement>) => {
        dispatch({
            type: FormActions.setEmail,
            payload: e.target.value
        });
    }
    const handleGithubChange = (e: ChangeEvent<HTMLInputElement>) => {
        dispatch({
            type: FormActions.setGithub,
            payload: e.target.value
        });
    }

    const [values, setValues] =useState({
        name: '',

    });

    function onChange(ev) {
        setValues({
            ...values,
            [ev.target.name]: ev.target.value,

        })
    }



    return (

        <Theme >
            <C.Container >
                <p>Passo 3/3</p>
                <h1>Bacana {state.name}, onde achamos vocÃª?</h1>
                <p>Preencha com seus contatos para conseguirmos entrar em contato.</p>

                <hr/>

                <label>
                    Qual seu e-mail?
                    <input
                        type="email"
                        value={state.email}
                        onChange={handleEmailChange}
                    />
                       <input email="email" type="text" value={values.name} onChange={onchange}/>

                </label>

                <label>
                    Qual seu Github?
                    <input
                        type="url"
                        value={state.github}
                        onChange={handleGithubChange}

                    />
                     <input github="github" type="text" value={values.github} onChange={onchange}/>

                </label>             

                <Link to="/step4" className="backButton">Finalizar Cadastro</Link>



            </C.Container>
        </Theme>
            );
        }
