import { useHistory, Link } from 'react-router-dom';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom'
import * as C from './styles';
import { useForm, FormActions } from '../../contexts/FormContext';
import { Theme } from '../../components/Theme';
import { ChangeEvent, useEffect } from 'react';
import { SelectOption } from '../../components/SelectOption';

export const FormStep2 = () => {
    const history = useHistory();
    const { state, dispatch } = useForm();

    useEffect(() => {
        if(state.name === '') {
            history.push('/');
        } else {
            dispatch({
                type: FormActions.setCurrentStep,
                payload: 2
            });
        }
    }, []);

    const handleNextStep = () => {
        if(state.name !== '') {
            history.push('/step3');
        } else {
            alert("Preencha os dados.");
        }
    }

    const setLevel = (level: number) => {
        dispatch({
            type: FormActions.setLevel,
            payload: level
        });
    }

    return (
        <Theme>
            <C.Container>
                <p>Passo 2/3</p>
                <h1>{state.name}, o que melhor descreve voc√™?</h1>
                <p>Escolha a op√ß√£o que melhor condiz com seu estado atual de estudante na Faculdade.</p>

                <hr/>

                <SelectOption
                    title="Comecei a faculdade agora"
                    description="Estou no terceiro semestre de Letras"
                    icon="ü•≥"
                    selected={state.level === 0}
                    onClick={()=>setLevel(0)}
                />

                <SelectOption
                    title="Sou Veterano"
                    description="J√° engressei na faculdade h√° 3 anos e dois meses"
                    icon="üòé"
                    selected={state.level === 1}
                    onClick={()=>setLevel(1)}
                />

                <Link to="/" className="backButton">Voltar</Link>
                <button onClick={handleNextStep}>Pr√≥ximo</button>
            </C.Container>
        </Theme>
    );
}
